#!/bin/bash

# Comprehensive test script for n8n chatbot with 30 questions
# Includes expected answers and automatic checking

WEBHOOK_URL="https://n8n.mainfact.ai/webhook/ac0c1794-2908-4204-acf9-06ad03419634/chat"
RESULTS_FILE="/tmp/chatbot_test_results.txt"
EXPECTED_FILE="/tmp/expected_answers.txt"

echo "=== n8n Chatbot Comprehensive Test - 30 Questions ==="
echo ""

# Initialize results files
> "$RESULTS_FILE"
> "$EXPECTED_FILE"

# Function to generate session ID
generate_session_id() {
    python3 -c "import uuid; print(uuid.uuid4())"
}

# Function to send question and capture response
send_question() {
    local question="$1"
    local expected="$2"
    local test_num="$3"
    local session_id=$(generate_session_id)
    
    echo "=== Test $test_num ==="
    echo "Frage: $question"
    echo "Session ID: $session_id"
    echo ""
    
    # Save expected answer
    echo "=== Test $test_num - Expected ===" >> "$EXPECTED_FILE"
    echo "$expected" >> "$EXPECTED_FILE"
    echo "" >> "$EXPECTED_FILE"
    
    JSON_PAYLOAD=$(cat <<EOF
{
  "sessionId": "$session_id",
  "action": "sendMessage",
  "chatInput": "$question"
}
EOF
)
    
    echo "Sending request..."
    echo "=== Test $test_num - Response ===" >> "$RESULTS_FILE"
    
    # Send request and capture response
    response=$(curl -s -X POST "$WEBHOOK_URL" \
      -H "Content-Type: application/json" \
      -d "$JSON_PAYLOAD")
    
    echo "$response" >> "$RESULTS_FILE"
    echo "" >> "$RESULTS_FILE"
    
    echo "Response: $response"
    echo "---"
    echo ""
    
    # Small delay between requests
    sleep 1
}


